// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "../../lib/ABDKMath64x64.sol";

import "./InterestMarket.sol";

/// @title Create a vault with compound interest
abstract contract CompoundInterest is InterestModel {
    using ABDKMath64x64 for int128;

    int128 private interestRate;
    uint256 private gracePeriod;

    function _setInterestRate(uint256 _yearlyInterestRate) override virtual internal {
        interestRate = ABDKMath64x64.add(
            ABDKMath64x64.divu(
                _yearlyInterestRate,
                10 ** _INTEREST_RATE_DECIMALS() * 365 days
            ),
            ABDKMath64x64.fromUInt(1)
        );
    }

    function APR() external view returns (uint) {
        uint year = 365 days;
        int128 apr = ABDKMath64x64.mul(interestRate.sub(ABDKMath64x64.fromUInt(1)), ABDKMath64x64.fromUInt(year));
        return ABDKMath64x64.mulu(apr, 10 ** _INTEREST_RATE_DECIMALS());
    }

    function APY() external view returns (uint){
        uint year = 365 days;
        return ABDKMath64x64.toUInt(interestRate.pow(year).sub(ABDKMath64x64.fromUInt(1)).mul(ABDKMath64x64.fromUInt(10 ** _INTEREST_RATE_DECIMALS())));
    }

    /// @dev compounds the interest every second and updates the interest index and vault interest index
    /// @notice elapsedSeconds Calculate the elapsed time in seconds since the last update
    function _compoundInterest() override virtual internal {
        //Early return if interestRate has been updates in this block already
        uint256 elapsedSeconds = _elapsedSeconds();
        if (elapsedSeconds == 0) {
            return;
        }
        uint256 roi = ABDKMath64x64.toUInt(
            interestRate.pow(elapsedSeconds).mul(
                ABDKMath64x64.fromUInt(10 ** _INTEREST_RATE_DECIMALS())
            )
        );
        _compoundROI(roi);
    }

    function _interestGrace(uint256 principal) override virtual internal view returns(uint256) {
        return principal * ABDKMath64x64.mulu(ABDKMath64x64.mul(interestRate, ABDKMath64x64.fromUInt(gracePeriod)), 10 ** _INTEREST_RATE_DECIMALS()) / 10 ** _INTEREST_RATE_DECIMALS();
    }
}

contracts/markets/interests/InterestMark.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "../CollaterizedMarket.sol";

import "hardhat/console.sol";

abstract contract InterestModel {
    function _setInterestRate(uint256 yearlyInterestRate) virtual internal;

    function _compoundInterest() virtual internal;

    function _compoundROI(uint256 _interestYielded) virtual internal;

    function _elapsedSeconds() virtual internal view returns(uint256);

    function _INTEREST_RATE_DECIMALS() virtual internal pure returns(uint8);

    function _interestGrace(uint256 principal) virtual internal view returns(uint256);
}

abstract contract InterestMarket is CollaterizedMarket, InterestModel {
    uint8 public constant INTEREST_RATE_DECIMALS = 18;

    uint256 private interestIndex;
    uint256 private lastUpdate; 
    mapping(uint256 => uint256) private accountInterestIndex;

    function getAccountInterestIndex(uint256 accountID) external view returns(uint256) {
        return accountInterestIndex[accountID];
    }

    constructor(string memory name, string memory symbol, string memory uri) CollaterizedMarket(name, symbol, uri){
        interestIndex = 10 ** INTEREST_RATE_DECIMALS;
        lastUpdate = block.timestamp;
    }

    function _INTEREST_RATE_DECIMALS() override internal pure returns(uint8){
        return INTEREST_RATE_DECIMALS;
    }

    function _compoundROI(uint256 _roi) override internal {
        interestIndex = interestIndex * _roi / 10 ** INTEREST_RATE_DECIMALS;
        lastUpdate = block.timestamp;
    }

    function _interestIndex() internal view returns(uint256) {
        return interestIndex;
    }

    function _elapsedSeconds() override internal view returns(uint256) {
        return block.timestamp - lastUpdate;
    }

    function _principalAmount(uint256 _accountID) override internal view returns (uint256) {
        if (accountInterestIndex[_accountID] == 0) {
                return 0;
        }
        return CollaterizedMarket._principalAmount(_accountID) * interestIndex / accountInterestIndex[_accountID];
    }

    function _increasePrincipalAmount(uint256 accountID, uint256 amount) virtual internal returns (uint){
        uint256 pa = _principalAmount(accountID) + amount;
        _setPrincipalAmount(accountID, pa);
        accountInterestIndex[accountID] = interestIndex;
        return pa;
    }

    function _decreasePrincipalAmount(uint256 accountID, uint256 amount) virtual internal returns (uint){
        uint256 pa = _principalAmount(accountID);
        uint256 graceInterest = _interestGrace(pa);
        require(pa >= amount, "Principal repaid is gt outstanding");
        pa -= amount;
        if (pa <= graceInterest){
            pa = 0;
        }
        _setPrincipalAmount(accountID, pa);
        accountInterestIndex[accountID] = interestIndex;
        return pa;
    }

    function _increaseCollateralAmount(uint256 accountID, uint256 amount) virtual internal returns(uint){
        uint collateral = _collateralAmount(accountID) + amount;
        _setCollateralAmount(accountID, collateral);
        return collateral;
    }

    function _decreaseCollateralAmount(uint256 accountID, uint256 amount) virtual internal returns(uint){
        uint collateral = _collateralAmount(accountID);
        require(collateral >= amount, "Account does not have enough collateral");
        collateral -= amount;
        _setCollateralAmount(accountID, collateral);
        return collateral;
    }
}

contracts/markets/interests/SimpleIntere.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "./InterestMarket.sol";

/// @title Create a vault with compound interest
abstract contract SimpleInterest is InterestMarket {

    uint256 private interestRate;
    uint256 private gracePeriod;

    event InterestAccrued(uint256 interestYield, uint256 timeElapsed);

    function _setInterestRate(uint256 _yearlyInterestRate) override virtual internal {
        _compoundInterest();
        interestRate =  _yearlyInterestRate / 365 days;
    }

    function APR() external view returns(uint256) {
        return interestRate * 365 days;
    }

    function roiSinceCompound() internal view returns(uint256) {
        return _elapsedSeconds() * interestRate + 10 ** _INTEREST_RATE_DECIMALS();
    }

    /// @dev compounds the interest every second and updates the interest index and vault interest index
    /// @notice elapsedSeconds Calculate the elapsed time in seconds since the last update
    function _compoundInterest() override virtual internal {
        uint256 roi = roiSinceCompound();
        _compoundROI(roi);
        emit InterestAccrued(roi, _elapsedSeconds());
    }

    function _getGracePeriod() internal view returns(uint256) {
        return gracePeriod;
    }

    function _setGracePeriod(uint256 grace) internal {
        gracePeriod = grace;
    }

    function _interestGrace(uint256 principal) override virtual internal view returns(uint256) {
        return principal * interestRate * gracePeriod / 10 ** _INTEREST_RATE_DECIMALS();
    }
}

